mapper.delete:
delete TBL_REPLY
   where repNum = #{repNum}
       and userId = #{userId}

mapper.update:
update TBL_REPLY
    set
        repCon = #{repCon}
    where
        repNum = #{repNum}
    and 
        userId = #{userId}

mapper.delete:
delete TBL_CART
    where cartNum = #{cartNum}
        and userId = #{userId}
